mapper.select:
select t.id, t.name as name, t.nick_name as nickName, t.mobile as mobile, t.remark as remark, t.user_id as userId, t.create_time as createTime, t.modify_time as modifyTime from weizu_oa_employee as t where id = #{id}
and name = #{name}
and user_id = #{userId}

select t.id, t.name as name, t.nick_name as nickName, t.mobile as mobile, t.remark as remark, t.user_id as userId, t.create_time as createTime, t.modify_time as modifyTime from weizu_oa_employee as t where 1=1
and t.name = #{name}
and t.user_id = #{userId}

select t.id, t.name as name, t.nick_name as nickName, t.mobile as mobile, t.remark as remark, t.user_id as userId, t.create_time as createTime, t.modify_time as modifyTime from weizu_oa_employee
and t.name LIKE CONCAT(CONCAT('%', #{pd.name}),'%')
and t.create_time >= #{pd.lastLoginStart}
and t.create_time <= #{pd.lastLoginEnd}
