mapper.update:
update  weizu_oa_employee_team
	set modify_time=now(),
		checked = #{checked}
	where
		id = #{id}

update  weizu_oa_employee_team
set modify_time=now(),
checked = 1
where 1=1

    and employee_id = #{employeeId}


    and team_id = #{teamId}

mapper.select:
select t.id, t.employee_id as employeeId, t.team_id as teamId, t.checked as checked, t.create_time as createTime, t.modify_time as modifyTime from weizu_oa_employee_team as t where 1=1
and t.employee_id = #{employeeId}
and t.team_id = #{teamId}
