mapper.select:
select request.id, request.user_id as userId, request.nick_name as nickName, request.request_info as requestInfo, us.avatar_url as avatarUrl, request.status as status, request.create_time as createTime from weizu_address_book_request request left join weizu_user us on request.user_id = us.id where 1 = 1
and request.nick_name LIKE CONCAT(CONCAT('%', #{pd.USERNAME}),'%')
and request.create_time >= #{pd.lastLoginStart}
and request.create_time <= #{pd.lastLoginEnd}
